import React from 'react'
import { useEffect, useState, useCallback } from 'react'

import { db } from "../../firebase"
import { async } from "@firebase/util"
import {  doc, 
          setDoc, 
          addDoc, 
          collection, 
          query, 
          where,
          getDocs, 
          onSnapshot,
          deleteDoc,
          updateDoc,
          QuerySnapshot,
          orderBy  } from "firebase/firestore"; 

import { Calendar, momentLocalizer } from "react-big-calendar";
import moment from "moment";
import "react-big-calendar/lib/css/react-big-calendar.css";
import './Cal.css'

import Title from '../Title.js'

const localizer = momentLocalizer(moment);

const CalendarScheduler = (props) => {

  const[events, setEvent] = useState([])

  useEffect(() => {
    let active = true
    if (active == true) {
      const q = query(collection(db, "calendar"))
      const getAllTasks = onSnapshot(q, (querySnapshot) => {
      let tasks = []
      querySnapshot.forEach((doc) => {
        tasks.push({...doc.data(), id:doc.id})
      })
      setEvent(() => tasks)
      console.log(curreevntTasks)

    })}
    return () => {active = false}
  },[])

  console.log(currentTasks)


  const createNewEvent = async (e) => {
    let confirmEventTitle = window.prompt("Create a new event")
    if (confirmEventTitle) {
      let newEvent = {
        start : e.start,
        end: e.end,
        title: confirmEventTitle
      }
      let newEvents = [...events, newEvent]
      setEvent(() => newEvents)
      await addDoc(collection(db, "calendar"), newEvent)
    }
  }

  const deleteCurrentEvent = async (e) => {
    let confirmDeleteEvent = window.confirm("Delete this event?")
    if (confirmDeleteEvent) {
      let newEvents = events.filter((event) => event.title != e.title)
      setEvent(() => newEvents)
      console.log(e.title)
      await deleteDoc(doc(db, "calendar", "SoqzSqQVQl0MvmtfFkH9"))
    }
  }

  return (
      <div className="App"> 
      <Calendar
        defaultDate={moment().toDate()}
        defaultView="month"
        views={['month','week']}
        localizer={localizer}
        events={events}
        selectable
        onSelectSlot={createNewEvent}
        onSelectEvent={deleteCurrentEvent}
      />   
      </div>
    );
}



export default CalendarScheduler
